'use client';

import { motion, type Variants } from 'framer-motion';
import { Checkbox } from '@/components/ui/checkbox';
import { Badge } from '@/components/ui/badge';

const containerVariants: Variants = {
  hidden: {
    opacity: 0,
  },
  visible: {
    opacity: 1,
    transition: {
      staggerChildren: 0.1,
    },
  },
};

const badgeVariants: Variants = {
  hidden: {
    opacity: 0,
    scale: 0.8,
  },
  visible: {
    opacity: 1,
    scale: 1,
    transition: {
      duration: 0.6,
      ease: 'easeOut',
    },
  },
};

const headingVariants: Variants = {
  hidden: {
    opacity: 0,
    y: 20,
  },
  visible: {
    opacity: 1,
    y: 0,
    transition: {
      duration: 0.8,
      delay: 0.2,
      ease: 'easeOut',
    },
  },
};

const fadeInUpVariants: Variants = {
  hidden: {
    opacity: 0,
    y: 30,
  },
  visible: {
    opacity: 1,
    y: 0,
    transition: {
      duration: 0.6,
      ease: 'easeOut',
    },
  },
};

const scaleInVariants: Variants = {
  hidden: {
    opacity: 0,
    scale: 0.9,
  },
  visible: {
    opacity: 1,
    scale: 1,
    transition: {
      duration: 0.6,
      ease: 'easeOut',
    },
  },
};

const lineVariants: Variants = {
  hidden: {
    opacity: 0,
    scaleX: 0,
  },
  visible: {
    opacity: 1,
    scaleX: 1,
    transition: {
      duration: 0.6,
      ease: 'easeOut',
    },
  },
};

const features = [
  'Quick list creation in seconds',
  'Intuitive task management',
  'Seamless organization experience',
];

interface FeatureSection {
  title: string;
  description: string;
  image: string;
  imagePosition: 'left' | 'right';
}

const featureSections: FeatureSection[] = [
  {
    title: 'Effortless task building',
    description:
      'Build workflows and manage projects in seconds with our intuitive dashboard interface',
    image: 'https://placehold.co/550x400',
    imagePosition: 'right',
  },
  {
    title: 'Advanced workflow tools',
    description:
      'Streamline your work process with powerful automation and intelligent project tracking',
    image: 'https://placehold.co/550x400',
    imagePosition: 'left',
  },
  {
    title: 'Real-time synchronization',
    description:
      'Connect your data across platforms and sync updates instantly with powerful integration',
    image: 'https://placehold.co/550x400',
    imagePosition: 'right',
  },
];

export default function FeatureDetail() {
  return (
    <section className="relative py-16 sm:py-20 md:py-28 overflow-hidden">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <motion.div
          className="text-center mb-16 sm:mb-20"
          initial="hidden"
          whileInView="visible"
          viewport={{
            once: true,
            amount: 0.3,
          }}
          variants={containerVariants}
        >
          {/* Badge */}
          <motion.div
            className="flex items-center justify-center gap-2 sm:gap-3"
            variants={containerVariants}
          >
            <motion.div className="flex items-center" variants={lineVariants}>
              <div className="w-12 sm:w-20 h-px bg-gradient-to-l from-primary/30 to-transparent"></div>
            </motion.div>
            <motion.div variants={badgeVariants}>
              <Badge variant="hero" className="group">
                <span className="text-sm font-normal">key features</span>
              </Badge>
            </motion.div>
            <motion.div className="flex items-center" variants={lineVariants}>
              <div className="w-12 sm:w-20 h-px bg-gradient-to-r from-primary/30 to-transparent"></div>
            </motion.div>
          </motion.div>

          <motion.h2
            className="text-3xl sm:text-4xl md:text-5xl lg:text-6xl font-normal leading-tight text-foreground"
            variants={headingVariants}
          >
            Features that make the difference
          </motion.h2>
        </motion.div>

        {/* Feature Sections */}
        <div className="space-y-20 sm:space-y-28">
          {featureSections.map((section) => (
            <motion.div
              key={section.title}
              initial="hidden"
              whileInView="visible"
              viewport={{
                once: true,
                amount: 0.3,
              }}
              variants={containerVariants}
              className={`grid grid-cols-1 lg:grid-cols-2 gap-10 sm:gap-16 items-center ${
                section.imagePosition === 'left' ? 'lg:flex-row-reverse' : ''
              }`}
            >
              {/* Content */}
              <motion.div
                variants={fadeInUpVariants}
                className={`space-y-6 sm:space-y-8 ${
                  section.imagePosition === 'left'
                    ? 'lg:order-2'
                    : 'lg:order-1'
                }`}
              >
                <div className="space-y-4">
                  <div className="flex items-center gap-3">
                    <h3 className="text-2xl sm:text-3xl md:text-4xl font-normal text-foreground">
                      {section.title}
                    </h3>
                  </div>
                  <p className="text-base sm:text-lg text-muted-foreground leading-relaxed max-w-lg">
                    {section.description}
                  </p>
                </div>

                {/* Features List */}
                <div className="space-y-4">
                  {features.map((feature) => (
                    <motion.div
                      key={feature}
                      variants={fadeInUpVariants}
                      className="flex items-center gap-3"
                    >
                      <Checkbox
                        checked
                        className="h-5 w-5 rounded-full border-primary data-[state=checked]:bg-primary"
                      />
                      <span className="text-sm sm:text-base text-foreground">
                        {feature}
                      </span>
                    </motion.div>
                  ))}
                </div>
              </motion.div>

              {/* Image */}
              <motion.div
                variants={scaleInVariants}
                className={`relative ${
                  section.imagePosition === 'left'
                    ? 'lg:order-1'
                    : 'lg:order-2'
                }`}
              >
                <div className="relative rounded-2xl sm:rounded-3xl overflow-hidden bg-card border border-border shadow-[0px_-4px_100px_21px_rgba(11,56,88,0.16)_inset]">
                  <img
                    src={section.image}
                    alt={section.title}
                    className="w-full h-64 sm:h-80 lg:h-96 object-cover"
                  />
                  <div className="absolute inset-0 bg-gradient-to-t from-card/50 to-transparent" />
                </div>
              </motion.div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
}

import type * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const badgeVariants = cva(
  "inline-flex items-center justify-center rounded-md border px-2 py-0.5 text-xs font-medium w-fit whitespace-nowrap shrink-0 [&>svg]:size-3 gap-1 [&>svg]:pointer-events-none focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive transition-[color,box-shadow] overflow-hidden",
  {
    variants: {
      variant: {
        default: "border-transparent bg-primary text-primary-foreground [a&]:hover:bg-primary/90",
        secondary: "border-transparent bg-secondary text-secondary-foreground [a&]:hover:bg-secondary/90",
        destructive:
          "border-transparent bg-destructive text-white [a&]:hover:bg-destructive/90 focus-visible:ring-destructive/20 dark:focus-visible:ring-destructive/40 dark:bg-destructive/60",
        outline: "text-foreground [a&]:hover:bg-accent [a&]:hover:text-accent-foreground",
        hero: "border-border/50 bg-secondary/50 text-foreground backdrop-blur-sm px-4 py-2 rounded-full",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  },
)

function Badge({
  className,
  variant,
  asChild = false,
  ...props
}: React.ComponentProps<"span"> & VariantProps<typeof badgeVariants> & { asChild?: boolean }) {
  const Comp = asChild ? Slot : "span"

  return <Comp data-slot="badge" className={cn(badgeVariants({ variant }), className)} {...props} />
}

export { Badge, badgeVariants }
